(71, 1, '7', 'Verifikasi Platform Merdeka Mengajar (PMM) kepada PPPK Guru Formasi 2022', 'verifikasi-platform-merdeka-mengajar-pmm-kepada-pppk-guru-formasi-2022', '<p><span style=\"font-family:Calibri\"><span style=\"font-family:Calibri\">Halo Teman Dikpora!</span></span></p>\r\n\r\n<p><span style=\"font-family:Calibri\"><span style=\"font-family:Calibri\">Dalam rangka evaluasi kinerja PPPK</span><span style=\"font-family:Calibri\">&nbsp;Guru</span><span style=\"font-family:Calibri\">&nbsp;Formasi 2022, </span><span style=\"font-family:Calibri\">H</span><span style=\"font-family:Calibri\">ari Selasa 26 September 2023</span><span style=\"font-family:Calibri\">&nbsp;Dinas Dikpora</span><span style=\"font-family:Calibri\">&nbsp;Kab</span><span style=\"font-family:Calibri\">upaten</span><span style=\"font-family:Calibri\">&nbsp;Wonosobo mengadakan kegiatan Verifikasi PMM </span><span style=\"font-family:Calibri\">kepada </span><span style=\"font-family:Calibri\">587 </span><span style=\"font-family:Calibri\">PPPK Guru yang b</span><span style=\"font-family:Calibri\">ertempat di Aula SKB Wonosobo</span><span style=\"font-family:Calibri\">. K</span><span style=\"font-family:Calibri\">egiatan </span><span style=\"font-family:Calibri\">ini </span><span style=\"font-family:Calibri\">bekerja sama dengan komunitas Koding Wonosobo&nbsp;</span><span style=\"font-family:Calibri\">yang bertujuan untuk</span><span style=\"font-family:Calibri\">&nbsp;memotivasi PPPK </span><span style=\"font-family:Calibri\">agar</span><span style=\"font-family:Calibri\">&nbsp;selalu belajar meningkatkan kompetensi mengajarnya</span>&nbsp;<span style=\"font-family:Calibri\">melalui pemanfaatan PMM yang memudahkan para pendidik mengakses informasi, referensi serta inspirasi. </span></span></p>\r\n\r\n<p><span style=\"font-family:Calibri\"><span style=\"font-family:Calibri\">Sebanyak 587 PPPK guru diperiksa</span><span style=\"font-family:Calibri\">&nbsp;pemenuhan minimal 6 aksi nyata PMM, </span><span style=\"font-family:Calibri\">mendapatkan </span><span style=\"font-family:Calibri\">coaching serta </span><span style=\"font-family:Calibri\">di</span><span style=\"font-family:Calibri\">wawancara tentang implementasi kurikulum merdeka</span><span style=\"font-family:Calibri\">.</span></span></p>\r\n\r\n<p><span style=\"font-family:Calibri\"><span style=\"font-family:Calibri\">#dikporawonosobo #verifikasiPMM #PlatformMedekaMengajar #PPPKguru2022</span></span></p>\r\n', '', 'https://dikpora.wonosobokab.go.id/upload/img/1698370533_ff891cb16e232435375b_thumb.jpeg', '2023-09-26 08:35:33', '2023-09-26 08:35:33', 39, '1'),
(72, 1, '7', 'Bimtek Iklim Kebhinekaan dan Iklim Inklusivitas Bagi Kepala SD Se- Kabupaten Wonosobo', 'bimtek-iklim-kebhinekaan-dan-iklim-inklusivitas-bagi-kepala-sd-se-kabupaten-wonosobo', '<p><span style=\"font-family:Calibri\"><span style=\"font-family:Calibri\">Halo Teman Dikpora!</span></span></p>\r\n\r\n<p><span style=\"font-family:Calibri\"><span style=\"font-family:Calibri\">Hari Senin</span><span style=\"font-family:Calibri\">&nbsp;s.d </span><span style=\"font-family:Calibri\">Selasa</span><span style=\"font-family:Calibri\">, </span><span style=\"font-family:Calibri\">tanggal 25-26 September 2023 Dinas Dikpora Kab</span><span style=\"font-family:Calibri\">upaten </span><span style=\"font-family:Calibri\">Wonosobo melaksanakan Bim</span><span style=\"font-family:Calibri\">bingan T</span><span style=\"font-family:Calibri\">ek</span><span style=\"font-family:Calibri\">nis</span><span style=\"font-family:Calibri\">&nbsp;Iklim Kebhinekaan dan Iklim Inklusivitas bagi Kepala</span>&nbsp;<span style=\"font-family:Calibri\">SD Se-Kabupaten Wonosobo</span><span style=\"font-family:Calibri\">&nbsp;yang bertempat</span><span style=\"font-family:Calibri\">&nbsp;di aula SD Negeri 2 Wonosobo</span><span style=\"font-family:Calibri\">. Kegiatan </span><span style=\"font-family:Calibri\">dimulai pukul 08.00 WIB &nbsp;sampai dengan 15.00 WIB</span><span style=\"font-family:Calibri\">&nbsp;dengan peserta sebanyak </span><span style=\"font-family:Calibri\">177 </span><span style=\"font-family:Calibri\">orang dan dibagi menjadi 2 hari. Peserta hari</span><span style=\"font-family:Calibri\">&nbsp;pertama </span><span style=\"font-family:Calibri\">ber</span><span style=\"font-family:Calibri\">jumlah 89 </span><span style=\"font-family:Calibri\">orang dan </span><span style=\"font-family:Calibri\">hari kedua 88</span><span style=\"font-family:Calibri\">&nbsp;orang.</span></span></p>\r\n\r\n<p><span style=\"font-family:Calibri\"><span style=\"font-family:Calibri\">Nara sumber Bimtek </span><span style=\"font-family:Calibri\">mengundang</span><span style=\"font-family:Calibri\">&nbsp;Ibu Widiyanti dari Pusat Penguatan Karakter ( PUSPEKA ) Kemendikbud ristek</span><span style=\"font-family:Calibri\">&nbsp;dan Bapak</span><span style=\"font-family:Calibri\">&nbsp;Slamet Faizi S.Pd.M.Pd dari Dinas </span><span style=\"font-family:Calibri\">D</span><span style=\"font-family:Calibri\">ikpora Kab</span><span style=\"font-family:Calibri\">upaten </span><span style=\"font-family:Calibri\">Wonosob</span><span style=\"font-family:Calibri\">o.</span></span></p>\r\n\r\n<p><span style=\"font-family:Calibri\"><span style=\"font-family:Calibri\">Tujuan Bimtek tersebut </span><span style=\"font-family:Calibri\">yakni a</span><span style=\"font-family:Calibri\">gar satuan pendidikan memahami adanya kesediaan untuk menerima kelompok lain secara sama sebagai kesatuan tanpa mempedulikan perbedaan budaya, etnik, bahasa dan agama</span><span style=\"font-family:Calibri\">, serta</span><span style=\"font-family:Calibri\">&nbsp;satuan pendidikan mampu menyelenggarakan iklim pembelajaran yang menerima dan menghargai perbedaan baik perbedaan sosial, budaya, agama dan suku bangsa</span><span style=\"font-family:Calibri\">.</span></span></p>\r\n\r\n<p><span style=\"font-family:Calibri\"><span style=\"font-family:Calibri\">Peserta bimtek antusias dalam menerima materi dari narasumber Puspeka dan optimis &nbsp;nilai A</span><span style=\"font-family:Calibri\">sesmen Nasional (AN)</span><span style=\"font-family:Calibri\">&nbsp;tahun 2023 </span><span style=\"font-family:Calibri\">akan mengalami peningkatan</span><span style=\"font-family:Calibri\">&nbsp;khususnya iklim Kebhinekaan dan iklim Inklusiavitas.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Calibri\"><span style=\"font-family:Calibri\">#dikporawonosobo #bimbinganteknis #iklimkebhinekaan #ikliminklusivitas</span></span></p>\r\n', '', 'https://dikpora.wonosobokab.go.id/upload/img/1698370585_e146ffe4650b1bfcd4c4_thumb.jpeg', '2023-09-26 08:36:25', '2023-09-26 08:36:25', 29, '1'),
(73, 1, '7', 'Guru Penggerak Berbagi Kabupaten Wonosobo', 'guru-penggerak-berbagi-kabupaten-wonosobo', '<p><span style=\"font-family:Calibri\"><span style=\"font-family:Calibri\">Halo Teman Dikpora!</span></span></p>\r\n\r\n<p><span style=\"font-family:Calibri\"><span style=\"font-family:Calibri\">Dinas Pendidikan, Pemuda dan Olahraga Kabupaten Wonosobo pada hari Sabtu, </span><span style=\"font-family:Calibri\">30 September 2023</span><span style=\"font-family:Calibri\">&nbsp;menyelenggarakan kegiatan </span><span style=\"font-family:Calibri\">Guru Penggerak Berbagi Kab</span><span style=\"font-family:Calibri\">upaten </span><span style=\"font-family:Calibri\">Wonosobo</span><span style=\"font-family:Calibri\">. Peserta kegiatan adalah </span><span style=\"font-family:Calibri\">Guru Penggerak Angkatan 6 dan 7 sejumlah 119 orang</span><span style=\"font-family:Calibri\">&nbsp;serta</span><span style=\"font-family:Calibri\">&nbsp;Guru P</span><span style=\"font-family:Calibri\">PP</span><span style=\"font-family:Calibri\">K Kategori P2 dan P3</span>&nbsp;<span style=\"font-family:Calibri\">sejumlah 289 orang</span><span style=\"font-family:Calibri\">.</span></span></p>\r\n\r\n<p><span style=\"font-family:Calibri\"><span style=\"font-family:Calibri\">Bertempat di </span><span style=\"font-family:Calibri\">GOR Patriot</span>&nbsp;<span style=\"font-family:Calibri\">SMA</span>&nbsp;<span style=\"font-family:Calibri\">N</span><span style=\"font-family:Calibri\">egeri</span><span style=\"font-family:Calibri\">&nbsp;2 Wonosobo</span><span style=\"font-family:Calibri\">&nbsp;dan di hadiri </span><span style=\"font-family:Calibri\">Kepala Dinas, Kabid P</span><span style=\"font-family:Calibri\">endidik dan Tenaga Kependidikan</span><span style=\"font-family:Calibri\">, Kasi P</span><span style=\"font-family:Calibri\">endidik dan Tenaga Kependidikan </span><span style=\"font-family:Calibri\">SD</span><span style=\"font-family:Calibri\">, serta Kasi </span><span style=\"font-family:Calibri\">P</span><span style=\"font-family:Calibri\">endidik dan Tenaga Kependidikan </span><span style=\"font-family:Calibri\">SMP</span><span style=\"font-family:Calibri\">&nbsp;Dinas</span>&nbsp;<span style=\"font-family:Calibri\">Dikpora</span><span style=\"font-family:Calibri\">&nbsp;Kab</span><span style=\"font-family:Calibri\">upaten</span><span style=\"font-family:Calibri\">&nbsp;Wonosob</span><span style=\"font-family:Calibri\">o.</span></span></p>\r\n\r\n<p><span style=\"font-family:Calibri\"><span style=\"font-family:Calibri\">Kegiatan ini merupakan pengembangan program guru penggerak &nbsp;untuk mengimbaskan ilmu , pengalaman, dan potensi alumni diklat PGP angkatan 6 dan 7 kepada guru P</span><span style=\"font-family:Calibri\">PP</span><span style=\"font-family:Calibri\">K.</span></span></p>\r\n\r\n<p><span style=\"font-family:Calibri\"><span style=\"font-family:Calibri\">Para </span><span style=\"font-family:Calibri\">PPPK K</span><span style=\"font-family:Calibri\">ategori P2 dan P3 agar memiliki pemahaman dan peningkatan kompetensi dalam menerapkan pembelajaran kurikulum merdeka dan kebijakan kementrian lainnya.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Calibri\"><span style=\"font-family:Calibri\">#dikporawonosobo #gurupenggerakberbagi </span></span></p>\r\n', '', 'https://dikpora.wonosobokab.go.id/upload/img/1698370631_22f6ab3e946ec4ce28b1_thumb.jpeg', '2023-09-30 08:37:11', '2023-09-30 08:37:11', 58, '1'),
(74, 1, '7', 'Pembekalan Kepala Sekolah Baru Jenjang SD dan SMP Kabupaten Wonosobo', 'pembekalan-kepala-sekolah-baru-jenjang-sd-dan-smp-kabupaten-wonosobo', '<p><span style=\"font-family:Calibri\"><span style=\"font-size:10.5000pt\"><span style=\"background-color:#ffffff\"><span style=\"font-family:\'Segoe UI\'\"><span style=\"color:#000000\">Halo Teman Dikpora!</span></span></span></span><br />\r\n<span style=\"font-size:10.5000pt\"><span style=\"background-color:#ffffff\"><span style=\"font-family:\'Segoe UI\'\"><span style=\"color:#000000\">Dinas Pendidikan, Pemuda dan Olahraga Kabupaten Wonosobo mengadakan pembekalan kepada Kepala Sekolah baru jenjang SD dan SMP pada hari Selasa dan Rabu, 3-4 Oktober 2023 di Aula KH. Hasyim Asy&#39;ari Dinas Dikpora Kab. Wonosobo.</span></span></span></span><br />\r\n<span style=\"font-size:10.5000pt\"><span style=\"background-color:#ffffff\"><span style=\"font-family:\'Segoe UI\'\"><span style=\"color:#000000\">Kegiatan diikuti 19 Kepala Sekolah baru, 2 orang Pengawas SD dan 1 orang Pengawas SMP, serta pejabat di lingkungan Dinas Dikpora Kab. Wonosobo.</span></span></span></span><br />\r\n<span style=\"font-size:10.5000pt\"><span style=\"background-color:#ffffff\"><span style=\"font-family:\'Segoe UI\'\"><span style=\"color:#000000\">Tujuan kegiatan pembekalan ini agar Kepala Sekolah Baru paham tentang tugas dan fungsi Kepala Sekolah sehingga mampu memimpin satuan pendidikan dengan baik dan mampu meningkatkan kompetensinya sebagai Kepala Sekolah.</span></span></span></span><br />\r\n<span style=\"font-size:10.5000pt\"><span style=\"background-color:#ffffff\"><span style=\"font-family:\'Segoe UI\'\"><span style=\"color:#000000\">Dalam sambutanya, Kepala Dinas Dikpora Kab. Wonosobo Drs. Tono Prihatono sangat mengapresiasi para KS baru dan berharap usai memperoleh pembekalan ini para KS semakin mantap dan benar dalam memimpin satuan pendidikan masing-masing, memiliki pemahaman dalam menerapkan kurikulum merdeka dan kebijakan Kementerian Pendidikan, Kebudayaan, Riset dan Teknologi&nbsp;</span></span></span></span><span style=\"font-size:10.5000pt\"><span style=\"background-color:#ffffff\"><span style=\"font-family:\'Segoe UI\'\"><span style=\"color:#000000\">lainnya.</span></span></span></span><br />\r\n<span style=\"font-size:10.5000pt\"><span style=\"background-color:#ffffff\"><span style=\"font-family:\'Segoe UI\'\"><span style=\"color:#000000\">Materi hari pertama yakni BOSP, Arkas, Rapor Pendidikan, Perencanaan Berbasis Data, Pengelolaan Sarpras, Pembinaan PTK dan Tupoksi KS yang disampaikan oleh Kepala Dinas, Sekretaris Dinas, Para Kepala Bidang, dan Pengawas SD dan SMP</span></span></span></span><br />\r\n<span style=\"font-size:10.5000pt\"><span style=\"background-color:#ffffff\"><span style=\"font-family:\'Segoe UI\'\"><span style=\"color:#000000\">Hari ke-2 disampaikan materi Tupoksi KS dan Pemaparan oleh KS baru tentang Penanganan Rapor Pendidikan yg perlu perbaiki/benahi di satpen masing-masing.</span></span></span></span><br />\r\n<br />\r\n<a href=\"https://www.instagram.com/explore/tags/dikporawonosobo/\"><span style=\"font-size:10.5000pt\"><span style=\"background-color:#ffffff\"><span style=\"font-family:\'Segoe UI\'\"><span style=\"color:#0000ff\">#dikporawonosobo</span></span></span></span></a>&nbsp;<a href=\"https://www.instagram.com/explore/tags/pembekalankepalasekolah/\"><span style=\"font-size:10.5000pt\"><span style=\"background-color:#ffffff\"><span style=\"font-family:\'Segoe UI\'\"><span style=\"color:#0000ff\">#pembekalankepalasekolah</span></span></span></span></a></span></p>\r\n', '', 'https://dikpora.wonosobokab.go.id/upload/img/1698370720_e35ea166bc41ae70f864_thumb.jpeg', '2023-10-03 08:38:40', '2023-10-03 08:38:40', 58, '1'),
(75, 1, '7', 'Peningkatan dan Penguatan Kapastitas Pendidik TK Negeri Pembina', 'peningkatan-dan-penguatan-kapastitas-pendidik-tk-negeri-pembina', '<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri\"><span style=\"font-size:11.0000pt\"><span style=\"font-family:Calibri\">TK Negeri Pembina adalah salah satu lembaga pendidikan yang didirikan oleh pemerintah sebagai wadah pembinaan teknis operasional dalam rangka pembinaan dan pengembangan aspek-aspek moral, intelektual, sosial, emosional, Fisik Motorik dan seni bagi anak usia dini. Disamping itu TK N</span></span><span style=\"font-size:11.0000pt\"><span style=\"font-family:Calibri\">egeri</span></span><span style=\"font-size:11.0000pt\"><span style=\"font-family:Calibri\">&nbsp;Pembina juga diharapkan bisa berfungsi sebagai model/contoh baik proses pembelajaran maupun sarana dan prasarana bagi TK disekitarnya.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri\"><span style=\"font-size:11.0000pt\"><span style=\"font-family:Calibri\">Di Wonosobo terdapat 3 (tiga) TK Negeri Pembina yaitu TK Negeri Pembina Wonosobo, TK Negeri Pembina Selomerto, dan TK Negeri Pembina Sapuran. </span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri\"><span style=\"font-size:11.0000pt\"><span style=\"font-family:Calibri\">Untuk meningkatkan mutu SDM pendidik dan tenaga kependidikan di tiga TK Negeri Pembina tersebut, Dinas Pendidikan, Pemuda dan Olahraga Kabupaten Wonosobo melalui seksi PAUD, menyelenggarakan Bimbingan teknis Peningkatan dan Penguatan kapasitas Pendidik dan tenaga kependidikan TK Negeri Pembina yang dilaksanakan selama 3 hari, dari tanggal 17 s.d 19 Oktober 2023. Kegiatan Bimtek yang berlangsung di RM Sari Rasa itu dibuka oleh kepala Dinas Pendidikan, Pemuda dan Olahraga Kabupaten Wonosobo Drs. Tono Prihatono. Dalam sambutannya, beliau memberikan apresiasi kepada peserta bimtek yang terlihat penuh semangat dalam meningkatkan kompetensinya. Bapak Tono Prihatono juga memberikan apresiasi bagi pendidik TK N Pembina yang sudah &nbsp;berpartisipasi dalam program pemerintah dengan mengikuti Program Guru Penggerak. Saat ini TK N Pembina memiliki 2 (dua) orang Guru Penggerak dan 2 (dua) orang Calon Guru Penggerak (CGP). Diharapkan nantinya akan semakin banyak yang ikut Pendidiknya yang mendaftar CGP &nbsp;dan bahkan satuan pendidikannya bisa mengikuti Program Sekolah Penggerak (PSP).</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri\"><span style=\"font-size:11.0000pt\"><span style=\"font-family:Calibri\">Bimbingan teknis Peningkatan dan Penguatan kapasitas Pendidik dan tenaga kependidikan TK Negeri Pembina diisi dengan materi yang berkaitan dengan kurikulum merdeka PAUD dan praktik pembelajaran di PAUD menggunakan loosepart dengan nara sumber Kasi PAUD Eko Murdijanti, S.Pd. M. Pd., Dra. Erwin Roosilawati, M.Pd. dari BBGP Provinsi Jawa Tengah, dan Sisilia Maryati SPsi, MPd. Nara sumber nasional dari PP Mutiara Ibu Purworejo.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri\"><span style=\"font-size:11.0000pt\"><span style=\"font-family:Calibri\">#dikporawonosobo #bimtek #bimtekpendidik #TKnegeripembina</span></span></span></span></p>\r\n', '', 'https://dikpora.wonosobokab.go.id/upload/img/1698370830_8074c79739321af49bf6_thumb.jpeg', '2023-10-19 08:40:30', '2023-10-19 08:40:30', 44, '1'),
(76, 1, '7', 'Kegiatan Pencegahan Perundungan, Kekerasan Seksual dan Intoleransi di Satuan Pendidikan', 'kegiatan-pencegahan-perundungan-kekerasan-seksual-dan-intoleransi-di-satuan-pendidikan', '<p><span style=\"font-family:Calibri\"><span style=\"font-size:10.5000pt\"><span style=\"background-color:#ffffff\"><span style=\"font-family:\'Segoe UI\'\"><span style=\"color:#000000\">Maraknya berita tentang kasus kekerasan yang terjadi di lingkungan satuan pendidikan, Dinas Dikpora Kabupaten Wonosobo melaksanakan sosialisasi pencegahan kekerasan di lingkungan satuan pendidikan pada 18-19 Oktober 2023 bertempat di Aula SMP N 2 Wonosobo.</span></span></span></span><br />\r\n<span style=\"font-size:10.5000pt\"><span style=\"background-color:#ffffff\"><span style=\"font-family:\'Segoe UI\'\"><span style=\"color:#000000\">Diikuti seluruh kepala sekolah negeri dan swasta se-Kabupaten Wonosobo, kegiatan ini di pimpin langsung oleh Kepala Dinas Dikpora dan didukung oleh materi pendukung yang disampaikan oleh Kabid BPP, Kabid PTK, dan Kabid Kurikuklum.</span></span></span></span><br />\r\n<span style=\"font-size:10.5000pt\"><span style=\"background-color:#ffffff\"><span style=\"font-family:\'Segoe UI\'\"><span style=\"color:#000000\">Kepala Dinas Dikpora, menyampaikan bahwa kekerasan di sekolah sudah dalam keadaan &ldquo;darurat &ldquo; maka harus ada upaya-upaya dan langkah serius yang harus dilakukan oleh semua satpen dan untuk melakukan langkah-langkah cepat sebagai berikut:</span></span></span></span><br />\r\n<span style=\"font-size:10.5000pt\"><span style=\"background-color:#ffffff\"><span style=\"font-family:\'Segoe UI\'\"><span style=\"color:#000000\">1. Edukasi ulang tentang kekerasan seksual, perundungan dan bullying, para guru kelas/wali kelas agar melaksanakan edukasi langsung dengan contoh-contoh yang jelas. Beliau mencontohkan yang di lakukan oleh guru Galih Sulistyaningra (</span></span></span></span><a href=\"https://www.instagram.com/galihtyanr/\"><span style=\"font-size:10.5000pt\"><span style=\"background-color:#ffffff\"><span style=\"font-family:\'Segoe UI\'\"><span style=\"color:#0000ff\">@galihtyanr</span></span></span></span></a><span style=\"font-size:10.5000pt\"><span style=\"background-color:#ffffff\"><span style=\"font-family:\'Segoe UI\'\"><span style=\"color:#000000\">) seorang Guru di Engineering for Kids Indonesia, sebuah lembaga pendidikan berbasis STEAM dari US.</span></span></span></span><br />\r\n<span style=\"font-size:10.5000pt\"><span style=\"background-color:#ffffff\"><span style=\"font-family:\'Segoe UI\'\"><span style=\"color:#000000\">2. Para KS dan guru agar rutin memantau di tempat dan waktu yang rawan terjadinya perundungan. Seperti diwaktu istirahat, pulang dan berangkat sekolah. Serta di kantin, lorong kelas, belakang sekolah dan kamar mandi.</span></span></span></span><br />\r\n<span style=\"font-size:10.5000pt\"><span style=\"background-color:#ffffff\"><span style=\"font-family:\'Segoe UI\'\"><span style=\"color:#000000\">3. Mengedukasi melalui &ldquo;lagu anti bullying&rdquo; yang wajib dinyanyikan di setiap kelas</span></span></span></span><br />\r\n<span style=\"font-size:10.5000pt\"><span style=\"background-color:#ffffff\"><span style=\"font-family:\'Segoe UI\'\"><span style=\"color:#000000\">4. Membuka ruang obrolan antara guru dan siswa agar terjadi kedekatan emosional</span></span></span></span><br />\r\n<span style=\"font-size:10.5000pt\"><span style=\"background-color:#ffffff\"><span style=\"font-family:\'Segoe UI\'\"><span style=\"color:#000000\">5. Melibatkan siswa dalam pencegahan, dengan membentuk &ldquo;satgas&rdquo; anti perundungan dari siswa</span></span></span></span><br />\r\n<span style=\"font-size:10.5000pt\"><span style=\"background-color:#ffffff\"><span style=\"font-family:\'Segoe UI\'\"><span style=\"color:#000000\">6. Memasang poster serta membuat rekaman suara anti perundungan yang di putar secara terus menerus saat jam tertentu</span></span></span></span><br />\r\n<span style=\"font-size:10.5000pt\"><span style=\"background-color:#ffffff\"><span style=\"font-family:\'Segoe UI\'\"><span style=\"color:#000000\">7. Melakukan edukasi pada orang tua/wali dan meningkatkan kegiatan parenting</span></span></span></span><br />\r\n<span style=\"font-size:10.5000pt\"><span style=\"background-color:#ffffff\"><span style=\"font-family:\'Segoe UI\'\"><span style=\"color:#000000\">8. Melakukan deteksi dini pada anak berdasarkan hasil assesment non kognitif dan profil siswa</span></span></span></span><br />\r\n<span style=\"font-size:10.5000pt\"><span style=\"background-color:#ffffff\"><span style=\"font-family:\'Segoe UI\'\"><span style=\"color:#000000\">9. Memperhatikan rekomendasi dari rapor pendidikan terkait indikator iklim keamanan dan kebhinekaan.</span></span></span></span><br />\r\n<span style=\"font-size:10.5000pt\"><span style=\"background-color:#ffffff\"><span style=\"font-family:\'Segoe UI\'\"><span style=\"color:#000000\">Dengan kegiatan tersebut diharapkan kekerasan yang terjadi di lingkungan satuan pendidikan dapat dicegah, serta satuan pendidikan dapat membangun eksosistem yang aman bagi peserta didik.</span></span></span></span><br />\r\n<br />\r\n<a href=\"https://www.instagram.com/explore/tags/dikporawonosobo/\"><span style=\"font-size:10.5000pt\"><span style=\"background-color:#ffffff\"><span style=\"font-family:\'Segoe UI\'\"><span style=\"color:#0000ff\">#dikporawonosobo</span></span></span></span></a>&nbsp;<a href=\"https://www.instagram.com/explore/tags/pencegahankekerasan/\"><span style=\"font-size:10.5000pt\"><span style=\"background-color:#ffffff\"><span style=\"font-family:\'Segoe UI\'\"><span style=\"color:#0000ff\">#pencegahankekerasan</span></span></span></span></a></span></p>\r\n', '', 'https://dikpora.wonosobokab.go.id/upload/img/1698370879_cc33fbedad4b797e47ff_thumb.jpeg', '2023-10-19 08:41:19', '2023-10-19 08:41:19', 48, '1'),
(77, 1, '7', 'Pelatihan Pemanfaatan Sumber Belajar Digital dalam Pembelajaran Inovatif Bagi Bendahara SD se Kab. Wonosobo', 'pelatihan-pemanfaatan-sumber-belajar-digital-dalam-pembelajaran-inovatif-bagi-bendahara-sd-se-kab-wonosobo', '<p><span style=\"font-family:Calibri\"><span style=\"font-family:Calibri\">Halo Teman Dikpora!</span></span></p>\r\n\r\n<p><span style=\"font-family:Calibri\"><span style=\"font-family:Calibri\">Dinas Pendidikan, Pemuda dan Olahraga Kabupaten Wonosobo mengadakan pelatihan pemanfaatan media belajar bagi s</span><span style=\"font-family:Calibri\">emua bendahara sekolah</span><span style=\"font-family:Calibri\">&nbsp;negeri </span><span style=\"font-family:Calibri\">jenjang SD</span><span style=\"font-family:Calibri\">, sebanyak </span><span style=\"font-family:Calibri\">447 </span><span style=\"font-family:Calibri\">sekolah. </span><span style=\"font-family:Calibri\">Waktu </span><span style=\"font-family:Calibri\">kegiatan dilaksanakan </span><span style=\"font-family:Calibri\">selama 2 hari</span><span style=\"font-family:Calibri\">, pada senin dan selasa,</span><span style=\"font-family:Calibri\">&nbsp;23-24 Oktober 2023</span><span style=\"font-family:Calibri\">&nbsp;yang </span><span style=\"font-family:Calibri\">terbagi </span><span style=\"font-family:Calibri\">di </span><span style=\"font-family:Calibri\">Aula PGRI dan </span><span style=\"font-family:Calibri\">A</span><span style=\"font-family:Calibri\">ula SKB Wonosobo</span><span style=\"font-family:Calibri\">.</span></span></p>\r\n\r\n<p><span style=\"font-family:Calibri\"><span style=\"font-family:Calibri\">Latar belakang</span><span style=\"font-family:Calibri\">&nbsp;kegiatan pelatihan ini karena sebagian besar bendahara adalah guru, yang dimana t</span><span style=\"font-family:Calibri\">anggung jawab bendahara</span><span style=\"font-family:Calibri\">&nbsp;cukup berat, m</span><span style=\"font-family:Calibri\">ulai dari perencanaan, pelaksanaan, sampai pada pelaporan. </span><span style=\"font-family:Calibri\">Selain t</span><span style=\"font-family:Calibri\">ugas bendahara </span><span style=\"font-family:Calibri\">yang </span><span style=\"font-family:Calibri\">tidak boleh ditinggalkan, tugas sebagai guru </span><span style=\"font-family:Calibri\">juga tetap harus berjalan</span><span style=\"font-family:Calibri\">. Kedua pekerjaan harus ditunaikan tanpa mengesampingkan salah satunya</span><span style=\"font-family:Calibri\">. </span></span></p>\r\n\r\n<p><span style=\"font-family:Calibri\"><span style=\"font-family:Calibri\">Menyikapi </span><span style=\"font-family:Calibri\">persolaan </span><span style=\"font-family:Calibri\">yang terjadi</span><span style=\"font-family:Calibri\">,</span><span style=\"font-family:Calibri\">&nbsp;Dinas Pendidikan, Pemuda dan Olahraga Kabupaten Wonosobo </span><span style=\"font-family:Calibri\">dibantu </span><span style=\"font-family:Calibri\">dari teman-teman </span><span style=\"font-family:Calibri\">guru </span><span style=\"font-family:Calibri\">yang tergabung dalam Komunitas Digitalisasi dan Inovasi Pembelajaran Guru (Koding)</span><span style=\"font-family:Calibri\">, </span><span style=\"font-family:Calibri\">mengadakan pelatihan pemanfaatan media belajar</span><span style=\"font-family:Calibri\">&nbsp;guna</span><span style=\"font-family:Calibri\">&nbsp;membantu </span><span style=\"font-family:Calibri\">guru dalam </span><span style=\"font-family:Calibri\">memperkenalkan berbagai media pembelajaran.</span></span></p>\r\n\r\n<p><span style=\"font-family:Calibri\"><span style=\"font-family:Calibri\">Dengan pengenalan berbagai media pembelajaran tersebut dapat memperingan tugas </span><span style=\"font-family:Calibri\">bendahara yang juga </span><span style=\"font-family:Calibri\">sebagai guru.</span><span style=\"font-family:Calibri\">&nbsp;Harapannya para siswa</span><span style=\"font-family:Calibri\">&nbsp;tetap </span><span style=\"font-family:Calibri\">men</span><span style=\"font-family:Calibri\">dapat</span><span style=\"font-family:Calibri\">kan</span>&nbsp;<span style=\"font-family:Calibri\">fasilitas </span><span style=\"font-family:Calibri\">untuk belajar secara maksimal. </span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n', '', 'https://dikpora.wonosobokab.go.id/upload/img/1698370927_4b05a67bc152ed41a7bc_thumb.jpeg', '2023-10-24 08:42:07', '2023-10-24 08:42:07', 64, '1'),
